<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class General {	var $data;		public function __construct ($param)	{		/*Assign paramteres*/		$this->data["params"] = $param;				/*Set Local Timezone*/		date_default_timezone_set("Asia/Jakarta");				/* Set Brand Header Text */		$this->data["sitetitle"] = "TERMINALKU";				/* Load Directory Path */		$this->LoadDirectories();		$this->LoadControllers();	}		public function LoadDirectories()	{		/* Set Content Directory */		$this->data["dirasset"] = $this->data["params"]["base_url"] . "assets/";	}		public function LoadControllers()	{		$this->data["urlhome"] = $this->data["params"]["base_url"] . "home";		$this->data["urlgiwangan"] = $this->data["params"]["base_url"] . "terminal_tembakip/show/giwangan";		$this->data["urlsoekarno"] = $this->data["params"]["base_url"] . "terminal_tembakip/show/soekarno";		$this->data["urlpurabaya"] = $this->data["params"]["base_url"] . "terminal_tembakip/show/purabaya";		$this->data["urltirtonadi"] = $this->data["params"]["base_url"] . "terminal_tembakip/show/tirtonadi";				$this->data["getcctv"] = $this->data["params"]["base_url"] . "terminal_tembakip/getcctv";		$this->data["loginaction"] = $this->data["params"]["base_url"] . "user/loginAction";		$this->data["uploadImage"] = $this->data["params"]["base_url"] . "user/uploadImage";	}		public function readDirectory($dir)	{		$result = array();		$result["dir"] = $dir;		$extList = array("jpg", "jpeg", "png");				if (is_dir($dir)){			if ($dh = opendir($dir)){				$i = 0;				while (($file = readdir($dh)) !== false){					if ($file != "." &&						$file != ".." &&						in_array(strtolower(pathinfo($file, PATHINFO_EXTENSION)), $extList))					{						$result["file"][$i]["filename"] = $file;						$result["file"][$i]["ext"] = pathinfo($file, PATHINFO_EXTENSION);						$i++;					}				}				closedir($dh);			}		}				//var_dump($result);		return ($result);	}}